export default {
  name: 'ChatInput',
  props: ['username'],
  data: function () {
    return {
      message: null
    }
  },
  methods: {
    writeMessage: function () {
      console.log("writeMessage");
      this.$socket.emit('sendMessage', {
        username: this.username,
        message: this.message
      });
      this.message = null;
    }
  },
  components: {}
}
